import { useState, useEffect } from "react";
import Keypad from "./Keypad";
import Display from "./Display";
import "./App.css";
import "./theme1.css";
import "./theme2.css";
import "./theme3.css";

const themeMap: any = {
  theme1: "theme1.css",
  theme2: "theme2.css",
  theme3: "theme3.css",
};

function Calculator() {
  const [inputValue, setInputValue] = useState("");
  const [previousValue, setPreviousValue] = useState("");
  const [operator, setOperator] = useState("");
  const [theme, setTheme] = useState("theme1");

  const handleThemeChange = () => {
    // Define an array of available themes
    const themes = ["theme1", "theme2", "theme3"];

    // Find the index of the current theme in the themes array
    const currentIndex = themes.indexOf(theme);

    // Calculate the index of the next theme
    const nextIndex = (currentIndex + 1) % themes.length;

    // Get the next theme from the themes array
    const nextTheme = themes[nextIndex];

    // Update the theme state with the next theme
    setTheme(nextTheme);
  };


  useEffect(() => {
    // Get the CSS file name based on the selected theme
    const themeFileName = themeMap[theme];

    // Dynamically create a link element to load the CSS file
    const linkElement = document.createElement("link");
    linkElement.setAttribute("rel", "stylesheet");
    linkElement.setAttribute("type", "text/css");
    linkElement.setAttribute("href", themeFileName);

    // Append the link element to the document's head to apply the theme
    document.head.appendChild(linkElement);

    // Cleanup function to remove the link element when the component unmounts or the theme changes
    return () => {
      document.head.removeChild(linkElement);
    };
  }, [theme]);

  function handleButtonClick(id: string) {
    if (id === ".") {
      if (inputValue === "") {
        setInputValue("0" + id);
      } else if (!inputValue.includes(".")) {
        setInputValue(inputValue + id);
      }
    } else {
      setInputValue(inputValue + id);
    }
  }

  function handleDeleteButtonClick() {
    if (inputValue.length > 0) {
      setInputValue((prevValue) => prevValue.slice(0, -1));
    }
  }

  function handleOperationButtonClick(newOperator: string) {
    if (inputValue.length > 0) {
      setPreviousValue(inputValue);
      setInputValue("");
      setOperator(newOperator);
    }
  }

  function handleResetButtonClick() {
    setInputValue("");
  }

  function handleEqualButtonClick() {
    if (previousValue !== "" && inputValue !== "") {
      const num1 = parseFloat(previousValue);
      const num2 = parseFloat(inputValue);
      let result;

      switch (operator) {
        case "+":
          result = num1 + num2;
          break;
        case "-":
          result = num1 - num2;
          break;
        case "*":
          result = num1 * num2;
          break;
        case "/":
          result = num1 / num2;
          break;
        default:
          result = 0;
          break;
      }

      setInputValue(result.toString());
      setPreviousValue("");
      setOperator("");
    }
  }

  return (
    <div className="calculatordiv">
      <div className="firstdiv">
        <h3>Calc</h3>
        <div className="themediv">
          <p>THEME</p>
          <div>
            <div className="spandiv">
              <span>1</span>
              <span>2</span>
              <span>3</span>
            </div>
            <div className="toggle" onClick={handleThemeChange}>
              <div
                className={`dot ${theme === "theme2" ? "dot-theme2" : ""}`}
              ></div>
            </div>
          </div>
        </div>
      </div>
      <Display inputValue={inputValue} />
      <Keypad
        handleButtonClick={handleButtonClick}
        handleDeleteButtonClick={handleDeleteButtonClick}
        handleAddButtonClick={() => handleOperationButtonClick("+")}
        handleEqualButtonClick={handleEqualButtonClick}
        handleResetButtonClick={handleResetButtonClick}
        handleSubtractButtonClick={() => handleOperationButtonClick("-")}
        handleMultiplyButtonClick={() => handleOperationButtonClick("*")}
        handleDivideButtonClick={() => handleOperationButtonClick("/")}
      />
    </div>
  );
}

export default Calculator;
