import React, { useState } from 'react';
import './App.css';
import Calculator from './Calculator';
import styles1 from './theme1.module.css';
import styles2 from './theme2.module.css';
import styles3 from './theme3.module.css';

function App() {
  const [themeIndex, setThemeIndex] = useState(0);
  const themes = [styles1, styles2, styles3];
  const currentTheme = themes[themeIndex];

  const handleThemeChange = () => {
    setThemeIndex((prevIndex) => (prevIndex + 1) % themes.length);
  };

  return (
    <div className={`main ${currentTheme.default}`}>
      <Calculator theme={currentTheme} onThemeChange={handleThemeChange} />
    </div>
  );
}

export default App;
